mapper.select:
select *
from reimbursement_detail
where REIMBURSEMENT_NO =#{remd_no} AND STATUS = #{status}

mapper.update:
         UPDATE reimbursement_detail
		SET STATUS = #{status},
		MODIFYBY = #{modifyby},
		MODIFYON = now()
		 WHERE REIMBURSEMENT_DETAIL_ID = #{id}
		 AND REIMBURSEMENT_NO = #{remd_no}
