mapper.select:
SELECT COUNT(*) AS COUNT, trainjoinlist.departmentid AS departmentid FROM trainjoinlist RIGHT JOIN startprogram ON startprogram.startprogramid=trainjoinlist.startprogramid AND startprogram.programtype='BC039004' AND startprogram.isonline='BC032002' AND startprogram.mandatory='BC033001' AND YEAR(startprogram.starttheorydate) = #{year}
WHERE trainjoinlist.departmentid IS NOT NULL AND trainjoinlist.departmentid <> '' AND trainjoinlist.jointype ='正常'
GROUP BY trainjoinlist.departmentid ORDER BY trainjoinlist.departmentid

