update.foreach:
update annualleave
deduct_annual_leave_lastyear = #{item.deduct_annual_leave_lastyear},deduct_paid_leave_lastyear = #{item.deduct_paid_leave_lastyear}, remaining_annual_leave_lastyear = #{item.remaining_annual_leave_lastyear},remaining_paid_leave_lastyear = #{item.remaining_paid_leave_lastyear}, deduct_annual_leave_thisyear = #{item.deduct_annual_leave_thisyear},deduct_paid_leave_thisyear = #{item.deduct_paid_leave_thisyear}, remaining_annual_leave_thisyear = #{item.remaining_annual_leave_thisyear},remaining_paid_leave_thisyear = #{item.remaining_paid_leave_thisyear}
WHERE annualleave_id = #{item.annualleave_id}

mapper.select:
select APPLICATION_DATE applicationdate, USER_ID userid, RESTDAYS restdays from restView
where USER_ID = #{userid}
order by RESTDAYS
