mapper.select:
SELECT user_id, center_id, group_id, team_id, years, months, CASE WHEN max(recognitionstate) = 1 AND min(recognitionstate) =1 THEN '1' WHEN max(recognitionstate) = 1 AND min(IF(IFNULL(recognitionstate,'')='',1,recognitionstate)) = 1 THEN '1' ELSE '0' END AS recognitionstate FROM attendance WHERE status = '0'
AND years = #{years,jdbcType=VARCHAR}
AND months = #{months,jdbcType=VARCHAR}
AND owner = #{owner,jdbcType=VARCHAR}
and owner in
#{item}
GROUP BY user_id, center_id, group_id, team_id, years, months order by CAST(years as SIGNED) desc ,CAST(months as SIGNED) desc

select
sum(CONVERT(IFNULL(tshortsickleave,'0'),DECIMAL(6,2)) + CONVERT(IFNULL(shortsickleave,'0'),DECIMAL(6,2)))
from attendance
where attendance.dates >= DATE_FORMAT(DATE_SUB(#{dates},INTERVAL 1 YEAR),'%Y-%m-%d')
and attendance.dates <= #{dates}
and attendance.user_id = #{user_id}

mapper.update:
update attendance set RECOGNITIONSTATE = '1'
where USER_ID = #{user_id}
and MONTHS = #{months}
and YEARS = #{years}
and RECOGNITIONSTATE = '0'

update attendance set RECOGNITIONSTATE = '0'
where USER_ID = #{user_id}
and MONTHS = #{months}
and YEARS = #{years}
and RECOGNITIONSTATE = '1'

update attendance set RECOGNITIONSTATE = '1', MODIFYBY = #{user_id}, MODIFYON = NOW()
where ATTENDANCE_ID in
#{item.attendanceid}
