SELECT BPCOMPANY, CONVERT ( SUM( expensesolo1 ), DECIMAL ( 10, 2 )) AS expensesolo1, CONVERT ( SUM( expensesolo2 ), DECIMAL ( 10, 2 )) AS expensesolo2, CONVERT ( SUM( expensesolo3 ), DECIMAL ( 10, 2 )) AS expensesolo3, CONVERT ( SUM( expensesolo4 ), DECIMAL ( 10, 2 )) AS expensesolo4, CONVERT ( SUM( expensesolo5 ), DECIMAL ( 10, 2 )) AS expensesolo5, CONVERT ( SUM( expensesolo6 ), DECIMAL ( 10, 2 )) AS expensesolo6, CONVERT ( SUM( expensesolo7 ), DECIMAL ( 10, 2 )) AS expensesolo7, CONVERT ( SUM( expensesolo8 ), DECIMAL ( 10, 2 )) AS expensesolo8, CONVERT ( SUM( expensesolo9 ), DECIMAL ( 10, 2 )) AS expensesolo9, CONVERT ( SUM( expensesolo10 ), DECIMAL ( 10, 2 )) AS expensesolo10, CONVERT ( SUM( expensesolo11 ), DECIMAL ( 10, 2 )) AS expensesolo11, CONVERT ( SUM( expensesolo12 ), DECIMAL ( 10, 2 )) AS expensesolo12 FROM coststatistics WHERE 1 = 1
and GROUP_ID = #{groupid}
and YEARS = #{years}
GROUP BY BPCOMPANY ORDER BY GROUP_ID DESC
