mapper.select:
/*select exp.`EXPNAME` as BPNAME, sum(exp.`JANUARY`) as MANHOUR1, sum(exp.FEBRUARY) as MANHOUR2, sum(exp.MARCH) as MANHOUR3, sum(exp.APRIL) as MANHOUR4, sum(exp.MAY) as MANHOUR5, sum(exp.JUNE) as MANHOUR6, sum(exp.`JULY`) as MANHOUR7, sum(exp.AUGUST) as MANHOUR8, sum(exp.SEPTEMBER) as MANHOUR9, sum(exp.OCTOBER) as MANHOUR10, sum(exp.NOVEMBER) as MANHOUR11, sum(exp.DECEMBER) as MANHOUR12 from expatriatesinfor exp where exp.`STATUS` = '0' group by exp.`EXPNAME`*/ select exp.EXPATRIATESINFOR_ID as BPNAME,exp.EXPNAME as BPNAME1, sum(CONVERT(df.`MANHOUR1`,DECIMAL(10,2))) as MANHOUR1, sum(CONVERT(df.MANHOUR2,DECIMAL(10,2))) as MANHOUR2, sum(CONVERT(df.MANHOUR3,DECIMAL(10,2))) as MANHOUR3, sum(CONVERT(df.MANHOUR4,DECIMAL(10,2))) as MANHOUR4, sum(CONVERT(df.MANHOUR5,DECIMAL(10,2))) as MANHOUR5, sum(CONVERT(df.MANHOUR6,DECIMAL(10,2))) as MANHOUR6, sum(CONVERT(df.`MANHOUR7`,DECIMAL(10,2))) as MANHOUR7, sum(CONVERT(df.MANHOUR8,DECIMAL(10,2))) as MANHOUR8, sum(CONVERT(df.MANHOUR9,DECIMAL(10,2))) as MANHOUR9, sum(CONVERT(df.MANHOUR10,DECIMAL(10,2))) as MANHOUR10, sum(CONVERT(df.MANHOUR11,DECIMAL(10,2))) as MANHOUR11, sum(CONVERT(df.MANHOUR12,DECIMAL(10,2))) as MANHOUR12 ,df.years as years ,df.GROUP_ID as groupid from coststatistics df inner join expatriatesinfor exp on df.ACCOUNT = exp.ACCOUNT where df.status = '0' and df.years = #{year} and df.account is not null
and df.GROUP_ID = #{groupid}
group by exp.EXPATRIATESINFOR_ID,exp.EXPNAME,df.years,df.GROUP_ID

select c.*
from coststatistics as c
where 1=1
and c.years = #{year}

	and  c.GROUP_ID = #{groupid}

SELECT
	co1.bpcompany,
	case when co1.ex1manhour = '0.00' then '' else round(co1.ex1manhour,2) end ex1manhour0,
	case when co1.ex1cost = '0.00' then '' else round(co1.ex1cost,2) end ex1cost0,
	case when co1.ex1usercount = '0' then '' else co1.ex1usercount end ex1usercount0,
	case when co2.ex2manhour = '0.00' then '' else round(co2.ex2manhour,2) end ex2manhour0,
	case when co2.ex2cost = '0.00' then '' else round(co2.ex2cost,2) end ex2cost0,
	case when co2.ex2usercount = '0' then '' else co2.ex2usercount end ex2usercount0,
	'' as ex3manhour0,
	'' as ex3cost0,
	@manhourcount0 := round( co1.ex1manhour + co2.ex2manhour, 2 ) as manhourcount0,
	@manhourcount0 as bpmanhourcount,
	@costcount0 := round( co1.ex1cost + co2.ex2cost, 2 ) as  costcount0,
	@costcount0 as bpcostcount,
    '0' as ex3costcount
FROM
	(
		SELECT
			coststatistics.bpcompany,
			SUM( CASE WHEN ( coststatistics.GROUP_ID = #{groupid} AND ex1.EXPATRIATESINFOR_ID IS NOT NULL ) THEN ${manhour} ELSE 0 END ) AS ex1manhour,
			SUM( CASE WHEN ( coststatistics.GROUP_ID = #{groupid} AND ex1.EXPATRIATESINFOR_ID IS NOT NULL )
				THEN
					(
						CASE WHEN ( ${months} = 3 or ${months} = 6 or ${months} = 9 or ${months} = 12)
						THEN ${cost}

							+ ${expense}

						ELSE ${cost}
						END
					)
				ELSE 0 END ) AS ex1cost,
			SUM( CASE WHEN ( coststatistics.GROUP_ID = #{groupid} AND ex1.EXPATRIATESINFOR_ID IS NOT NULL
AND (
(
CASE WHEN ( ${months} = 3 or ${months} = 6 or ${months} = 9 or ${months} = 12)
THEN ${cost}

	+ ${expense}

ELSE ${cost}
END
) > 0
OR
(
	ex1.EXITS = 1
	AND ex1.GROUP_ID = #{groupid}
)
OR
(
	ex1.EXITS = 0
	AND DATE_FORMAT(ex1.EXITIME, '%Y-%m') >= DATE_FORMAT(concat( #{years},'-01' ), '%Y-%m')
	AND ex1.GROUP_ID = #{groupid}
)
)
) THEN 1 ELSE 0 END ) AS ex1usercount
		FROM
			coststatistics
			LEFT JOIN expatriatesinfor ex1 ON coststatistics.bpname = ex1.expatriatesinfor_id
			AND ex1.jobclassification IN ('BP025001','BP025002','BP025005' )
		WHERE coststatistics.years = substring(#{years},1,4)
		GROUP BY bpcompany
	) co1
	LEFT JOIN (
		SELECT
			coststatistics.bpcompany,
			SUM( CASE WHEN ( coststatistics.GROUP_ID = #{groupid} AND ex2.EXPATRIATESINFOR_ID IS NOT NULL ) THEN ${manhour} ELSE 0 END ) AS ex2manhour,
			SUM( CASE WHEN ( coststatistics.GROUP_ID = #{groupid} AND ex2.EXPATRIATESINFOR_ID IS NOT NULL )
				THEN
					(
						CASE WHEN ( ${months} = 3 or ${months} = 6 or ${months} = 9 or ${months} = 12)
						THEN ${cost}

							+ ${expense}

ELSE ${cost}
END
)
ELSE 0 END ) AS ex2cost,
SUM( CASE WHEN ( coststatistics.GROUP_ID = #{groupid} AND ex2.EXPATRIATESINFOR_ID IS NOT NULL
AND (
(
CASE WHEN ( ${months} = 3 or ${months} = 6 or ${months} = 9 or ${months} = 12)
THEN ${cost}

	+ ${expense}

ELSE ${cost}
END
) > 0
OR
(
	ex2.EXITS = 1
	AND ex2.GROUP_ID = #{groupid}
)
OR (
	ex2.EXITS = 0
	AND DATE_FORMAT(ex2.EXITIME, '%Y-%m') >= DATE_FORMAT(concat( #{years},'-01' ), '%Y-%m')
	AND ex2.GROUP_ID = #{groupid}
)
)
) THEN 1 ELSE 0 END ) AS ex2usercount
FROM
coststatistics
LEFT JOIN expatriatesinfor ex2 ON coststatistics.bpname = ex2.expatriatesinfor_id
AND ex2.jobclassification IN ('BP025003','BP025004' )
WHERE coststatistics.years = substring(#{years},1,4)
GROUP BY bpcompany
) co2 ON co1.bpcompany = co2.bpcompany
order by co1.bpcompany

SELECT wf1.workflowinstanceid FROM workflowinstance wf1 WHERE NOT EXISTS ( SELECT 1 FROM workflowinstance wf2 WHERE wf2.DATAID = wf1.DATAID AND wf2.CREATEON > wf1.CREATEON ) AND wf1.STATUS = '4'
AND wf1.DATAID IN
#{item}

SELECT BPCOMPANY, CONVERT ( SUM( expense1 ), DECIMAL ( 10, 2 )) AS expense1, CONVERT ( SUM( expense2 ), DECIMAL ( 10, 2 )) AS expense2, CONVERT ( SUM( expense3 ), DECIMAL ( 10, 2 )) AS expense3, CONVERT ( SUM( expense4 ), DECIMAL ( 10, 2 )) AS expense4, CONVERT ( SUM( expense5 ), DECIMAL ( 10, 2 )) AS expense5, CONVERT ( SUM( expense6 ), DECIMAL ( 10, 2 )) AS expense6, CONVERT ( SUM( expense7 ), DECIMAL ( 10, 2 )) AS expense7, CONVERT ( SUM( expense8 ), DECIMAL ( 10, 2 )) AS expense8, CONVERT ( SUM( expense9 ), DECIMAL ( 10, 2 )) AS expense9, CONVERT ( SUM( expense10 ), DECIMAL ( 10, 2 )) AS expense10, CONVERT ( SUM( expense11 ), DECIMAL ( 10, 2 )) AS expense11, CONVERT ( SUM( expense12 ), DECIMAL ( 10, 2 )) AS expense12 FROM coststatistics WHERE 1 = 1
and GROUP_ID = #{groupid}
and YEARS = #{years}
GROUP BY BPCOMPANY ORDER BY GROUP_ID DESC
