mapper.select:
SELECT a.rank, a.group_id, a.project_id, sum( a.time_start ) time_start FROM ( SELECT pm.RANK, cd.TIME_START, cd.GROUP_ID, cd.PROJECT_ID FROM ( SELECT USER_ID AS userid, ROUND( ADJUST / my_WorkingDays ( DATE_FORMAT( concat( LOG_DATE, '-01' ), '%Y%m' ) ), 2 ) TIME_START, DEPARTMENT AS GROUP_ID, PROJECT_ID FROM logpersonstatistics lg WHERE DATE_FORMAT( concat( LOG_DATE, '-01' ), '%Y-%m' ) = #{LOG_DATE} AND PROJECT_ID != 'PP024001' AND PROJECT_ID IN
#{projectid}
GROUP BY LOG_DATE, USER_ID, ADJUST, GROUP_ID, PROJECT_ID ) cd INNER JOIN projectsystem pm ON pm.`NAME` = cd.userid AND pm.COMPANYPROJECTS_ID = cd.PROJECT_ID AND pm.`TYPE` = '0' INNER JOIN companyprojects cs ON cs.COMPANYPROJECTS_ID = cd.PROJECT_ID UNION ALL SELECT p.RANK, 0 TIME_START, CASE
WHEN c.CENTER_ID IN
#{dep} THEN c.CENTER_ID ELSE c.GROUP_ID END, c.COMPANYPROJECTS_ID FROM projectsystem p INNER JOIN companyprojects c ON p.COMPANYPROJECTS_ID = c.COMPANYPROJECTS_ID AND p.type = '0' AND p.COMPANYPROJECTS_ID IN
#{projectid}
WHERE c.CENTER_ID IN
#{dep}
OR c.GROUP_ID IN
#{dep} UNION ALL SELECT ade.ATTF, 0 TIME_START, ade.INCONDEPARTMENT, pc.COMPANYPROJECTS_ID FROM projectcontract pc INNER JOIN award ad ON pc.contract = ad.CONTRACTNUMBER AND pc.COMPANYPROJECTS_ID IN
#{projectid}
INNER JOIN staffdetail ade ON ade.AWARD_ID = ad.AWARD_ID AND ade.INCONDEPARTMENT IN
#{dep}
) a GROUP BY a.rank, a.group_id, a.project_id
