SELECT theme.THEMEINFOR_ID themeinforId, theme.THEMENAME themeName, theme.TOOLSORGS customerName FROM themeinfor theme LEFT JOIN themeplandetail detail ON theme.THEMEINFOR_ID = detail.THEMEINFOR_ID
AND detail.CENTER_ID = #{deptId}
AND detail.YEAR = #{year}
detail.THEMEPLANDETAIL_ID IS NULL
AND theme.YEAR = #{year}
GROUP BY theme.THEMEINFOR_ID

SELECT theme.THEMEINFOR_ID themeinforId, theme.THEMENAME themeName, theme.TOOLSORGS customerName, detail.TYPE type, detail.CONTRACTTYPE contracttype, detail.PERSONNEL4 hoursPlan4, detail.AMOUNT4 moneyPlan4, detail.PERSONNEL5 hoursPlan5, detail.AMOUNT5 moneyPlan5, detail.PERSONNEL6 hoursPlan6, detail.AMOUNT6 moneyPlan6, detail.PERSONNEL7 hoursPlan7, detail.AMOUNT7 moneyPlan7, detail.PERSONNEL8 hoursPlan8, detail.AMOUNT8 moneyPlan8, detail.PERSONNEL9 hoursPlan9, detail.AMOUNT9 moneyPlan9, detail.PERSONNEL10 hoursPlan10, detail.AMOUNT10 moneyPlan10, detail.PERSONNEL11 hoursPlan11, detail.AMOUNT11 moneyPlan11, detail.PERSONNEL12 hoursPlan12, detail.AMOUNT12 moneyPlan12, detail.PERSONNEL1 hoursPlan1, detail.AMOUNT1 moneyPlan1, detail.PERSONNEL2 hoursPlan2, detail.AMOUNT2 moneyPlan2, detail.PERSONNEL3 hoursPlan3, detail.AMOUNT3 moneyPlan3 FROM themeinfor theme INNER JOIN themeplandetail detail ON theme.THEMEINFOR_ID = detail.THEMEINFOR_ID
AND detail.CENTER_ID = #{deptId}
AND detail.YEAR = #{year}
detail.THEMEPLANDETAIL_ID IS NOT NULL
AND theme.YEAR = #{year}
GROUP BY theme.THEMEINFOR_ID,theme.THEMENAME,theme.TOOLSORGS, detail.TYPE,detail.CONTRACTTYPE,detail.PERSONNEL4,detail.AMOUNT4, PERSONNEL5,detail.AMOUNT5,PERSONNEL6,detail.AMOUNT6,PERSONNEL7,detail.AMOUNT7, PERSONNEL8,detail.AMOUNT8,PERSONNEL9,detail.AMOUNT9,PERSONNEL10,detail.AMOUNT10, PERSONNEL11,detail.AMOUNT11,PERSONNEL12,detail.AMOUNT12,PERSONNEL1,detail.AMOUNT1, PERSONNEL2,detail.AMOUNT2,PERSONNEL3,detail.AMOUNT3

SELECT exp.id id, exp.themeinfor_id themeinforId, exp.class_ification classIfication, theme.THEMENAME themeName, theme.TOOLSORGS customerName, exp.save_date saveDate, exp.dept_id deptId, exp.hours_plan4 hoursPlan4, exp.money_plan4 moneyPlan4, exp.hours_forecast4 hoursForecast4, exp.money_forecast4 moneyForecast4, exp.hours_actual4 hoursActual4, exp.money_actual4 moneyActual4, exp.hours_plan5 hoursPlan5, exp.money_plan5 moneyPlan5, exp.hours_forecast5 hoursForecast5, exp.money_forecast5 moneyForecast5, exp.hours_actual5 hoursActual5, exp.money_actual5 moneyActual5, exp.hours_plan6 hoursPlan6, exp.money_plan6 moneyPlan6, exp.hours_forecast6 hoursForecast6, exp.money_forecast6 moneyForecast6, exp.hours_actual6 hoursActual6, exp.money_actual6 moneyActual6, exp.hours_plan7 hoursPlan7, exp.money_plan7 moneyPlan7, exp.hours_forecast7 hoursForecast7, exp.money_forecast7 moneyForecast7, exp.hours_actual7 hoursActual7, exp.money_actual7 moneyActual7, exp.hours_plan8 hoursPlan8, exp.money_plan8 moneyPlan8, exp.hours_forecast8 hoursForecast8, exp.money_forecast8 moneyForecast8, exp.hours_actual8 hoursActual8, exp.money_actual8 moneyActual8, exp.hours_plan9 hoursPlan9, exp.money_plan9 moneyPlan9, exp.hours_forecast9 hoursForecast9, exp.money_forecast9 moneyForecast9, exp.hours_actual9 hoursActual9, exp.money_actual9 moneyActual9, exp.hours_plan10 hoursPlan10, exp.money_plan10 moneyPlan10, exp.hours_forecast10 hoursForecast10, exp.money_forecast10 moneyForecast10, exp.hours_actual10 hoursActual10, exp.money_actual10 moneyActual10, exp.hours_plan11 hoursPlan11, exp.money_plan11 moneyPlan11, exp.hours_forecast11 hoursForecast11, exp.money_forecast11 moneyForecast11, exp.hours_actual11 hoursActual11, exp.money_actual11 moneyActual11, exp.hours_plan12 hoursPlan12, exp.money_plan12 moneyPlan12, exp.hours_forecast12 hoursForecast12, exp.money_forecast12 moneyForecast12, exp.hours_actual12 hoursActual12, exp.money_actual12 moneyActual12, exp.hours_plan1 hoursPlan1, exp.money_plan1 moneyPlan1, exp.hours_forecast1 hoursForecast1, exp.money_forecast1 moneyForecast1, exp.hours_actual1 hoursActual1, exp.money_actual1 moneyActual1, exp.hours_plan2 hoursPlan2, exp.money_plan2 moneyPlan2, exp.hours_forecast2 hoursForecast2, exp.money_forecast2 moneyForecast2, exp.hours_actual2 hoursActual2, exp.money_actual2 moneyActual2, exp.hours_plan3 hoursPlan3, exp.money_plan3 moneyPlan3, exp.hours_forecast3 hoursForecast3, exp.money_forecast3 moneyForecast3, exp.hours_actual3 hoursActual3, exp.money_actual3 moneyActual3, exp.CREATEBY createby, exp.CREATEON createon, exp.MODIFYBY modifyby, exp.MODIFYON modifyon, exp.OWNER owner, exp.STATUS status, exp.TENANTID tenantid FROM expenditure_forecast exp LEFT JOIN themeinfor theme ON exp.themeinfor_id = theme.THEMEINFOR_ID
AND DATE_FORMAT(exp.save_date, '%Y%M') = DATE_FORMAT(#{saveDate}, '%Y%M')
AND exp.dept_id = #{deptId}
AND exp.annual = #{year}
AND theme.THEMENAME like CONCAT('%',#{themeName},'%')
order by theme.THEMENAME,exp.class_ification

SELECT IFNULL(ROUND(SUM(result.HOUR), 2),0) AS HOURS, IFNULL(ROUND( SUM( result.MONEY ), 2 ),0) AS MONEY FROM ( SELECT fri.HOUR, CASE WHEN DATE_FORMAT( concat( #{logDate}, '-01' ), '%m' ) = '04' THEN IFNULL( fri.HOUR, 0 ) * fee.month4 WHEN DATE_FORMAT( concat( #{logDate}, '-01' ), '%m' ) = '05' THEN IFNULL( fri.HOUR, 0 ) * fee.month5 WHEN DATE_FORMAT( concat( #{logDate}, '-01' ), '%m' ) = '06' THEN IFNULL( fri.HOUR, 0 ) * fee.month6 WHEN DATE_FORMAT( concat( #{logDate}, '-01' ), '%m' ) = '07' THEN IFNULL( fri.HOUR, 0 ) * fee.month7 WHEN DATE_FORMAT( concat( #{logDate}, '-01' ), '%m' ) = '08' THEN IFNULL( fri.HOUR, 0 ) * fee.month8 WHEN DATE_FORMAT( concat( #{logDate}, '-01' ), '%m' ) = '09' THEN IFNULL( fri.HOUR, 0 ) * fee.month9 WHEN DATE_FORMAT( concat( #{logDate}, '-01' ), '%m' ) = '10' THEN IFNULL( fri.HOUR, 0 ) * fee.month10 WHEN DATE_FORMAT( concat( #{logDate}, '-01' ), '%m' ) = '11' THEN IFNULL( fri.HOUR, 0 ) * fee.month11 WHEN DATE_FORMAT( concat( #{logDate}, '-01' ), '%m' ) = '12' THEN IFNULL( fri.HOUR, 0 ) * fee.month12 WHEN DATE_FORMAT( concat( #{logDate}, '-01' ), '%m' ) = '01' THEN IFNULL( fri.HOUR, 0 ) * fee.month1 WHEN DATE_FORMAT( concat( #{logDate}, '-01' ), '%m' ) = '02' THEN IFNULL( fri.HOUR, 0 ) * fee.month2 ELSE IFNULL( fri.HOUR, 0 ) * fee.month3 END MONEY -- fri.*,fee.* FROM ( SELECT res.COMPANYPROJECTS_ID, res.NAME, res.RANK, ROUND( l.DURATION / my_WorkingDays ( DATE_FORMAT( concat( #{logDate}, '-01' ), '%Y%m' ) ), 2 ) HOUR FROM ( SELECT t.`NAME`, t.RANK, c.COMPANYPROJECTS_ID FROM companyprojects c INNER JOIN `projectsystem` t ON t.COMPANYPROJECTS_ID = c.COMPANYPROJECTS_ID
AND ( c.CENTER_ID = #{deptId} OR c.GROUP_ID = #{deptId} )
INNER JOIN projectcontract p ON p.COMPANYPROJECTS_ID = c.COMPANYPROJECTS_ID
AND p.THEMEINFOR_ID = #{themeInforId}
WHERE 1 = 1
and t.TYPE = #{type}
GROUP BY t.`NAME`, t.RANK, c.COMPANYPROJECTS_ID ORDER BY c.PROJECT_NAME, t.RANK ) res LEFT JOIN logpersonstatistics l ON l.PROJECT_ID = res.COMPANYPROJECTS_ID AND res.`NAME` = l.USER_ID AND DATE_FORMAT( concat(l.LOG_DATE, '-01' ), '%Y-%m') = DATE_FORMAT( concat( #{logDate}, '-01' ), '%Y-%m') ORDER BY res.COMPANYPROJECTS_ID ) fri LEFT JOIN peoplewarefee fee ON fee.RANKS = fri.RANK
AND fee.`YEAR` = #{year}
AND fee.GROUP_ID = #{deptId}
) result

SELECT CASE WHEN DATE_FORMAT( concat( #{logDate}, '-01' ), '%m' ) = '04' THEN ROUND(SUM(p.APRIL),2) WHEN DATE_FORMAT( concat( #{logDate}, '-01' ), '%m' ) = '05' THEN ROUND(SUM(p.MAY),2) WHEN DATE_FORMAT( concat( #{logDate}, '-01' ), '%m' ) = '06' THEN ROUND(SUM(p.JULY),2) WHEN DATE_FORMAT( concat( #{logDate}, '-01' ), '%m' ) = '07' THEN ROUND(SUM(p.JULY),2) WHEN DATE_FORMAT( concat( #{logDate}, '-01' ), '%m' ) = '08' THEN ROUND(SUM(p.APRIL),2) WHEN DATE_FORMAT( concat( #{logDate}, '-01' ), '%m' ) = '09' THEN ROUND(SUM(p.SEPTEMBER),2) WHEN DATE_FORMAT( concat( #{logDate}, '-01' ), '%m' ) = '10' THEN ROUND(SUM(p.`OWNER`),2) WHEN DATE_FORMAT( concat( #{logDate}, '-01' ), '%m' ) = '11' THEN ROUND(SUM(p.NOVEMBER),2) WHEN DATE_FORMAT( concat( #{logDate}, '-01' ), '%m' ) = '12' THEN ROUND(SUM(p.DIVIDE),2) WHEN DATE_FORMAT( concat( #{logDate}, '-01' ), '%m' ) = '01' THEN ROUND(SUM(p.JANUARY),2) WHEN DATE_FORMAT( concat( #{logDate}, '-01' ), '%m' ) = '02' THEN ROUND(SUM(p.FEBRUARY),2) ELSE ROUND(SUM(p.MARCH),2) END MONEY FROM pjexternalinjection p
AND p.YEARS = #{year}
AND p.GROUP_ID = #{deptId}
AND p.THEMEINFOR_ID = #{themeInforId}

SELECT ex.class_ification as classIfication ,ROUND( IFNULL( SUM( ex.hours_plan4 ), 0 ), 2 ) as hoursPlan4 ,ROUND( IFNULL( SUM( ex.hours_plan5 ), 0 ), 2 ) as hoursPlan5 ,ROUND( IFNULL( SUM( ex.hours_plan6 ), 0 ), 2 ) as hoursPlan6 ,ROUND( IFNULL( SUM( ex.hours_plan7 ), 0 ), 2 ) as hoursPlan7 ,ROUND( IFNULL( SUM( ex.hours_plan8 ), 0 ), 2 ) as hoursPlan8 ,ROUND( IFNULL( SUM( ex.hours_plan9 ), 0 ), 2 ) as hoursPlan9 ,ROUND( IFNULL( SUM( ex.hours_plan10 ), 0 ), 2 ) as hoursPlan10 ,ROUND( IFNULL( SUM( ex.hours_plan11 ), 0 ), 2 ) as hoursPlan11 ,ROUND( IFNULL( SUM( ex.hours_plan12 ), 0 ), 2 ) as hoursPlan12 ,ROUND( IFNULL( SUM( ex.hours_plan1 ), 0 ), 2 ) as hoursPlan1 ,ROUND( IFNULL( SUM( ex.hours_plan2 ), 0 ), 2 ) as hoursPlan2 ,ROUND( IFNULL( SUM( ex.hours_plan3 ), 0 ), 2 ) as hoursPlan3 FROM expenditure_forecast ex
and ex.annual = #{year} AND ex.save_date LIKE concat(concat('%',#{year}),'-04%')
AND ex.dept_id = #{deptId}
AND ex.class_ification = #{classIfication}

SELECT IFNULL((
                  SELECT count(1) AS month04
                  FROM (SELECT count(*)
                        FROM attendance
                        WHERE years = #{year2} AND MONTHS = '04' AND CENTER_ID = #{deptname}
                           OR GROUP_ID = #{deptname}
                        GROUP BY USER_ID) AS month04
              ),
              0
           )        AS hoursActual4,
       IFNULL((
                  SELECT count(1) AS month05
                  FROM (SELECT count(*)
                        FROM attendance
                        WHERE years = #{year2}
                            AND MONTHS = '05' AND CENTER_ID = #{deptname}
                           OR GROUP_ID = #{deptname}
                        GROUP BY USER_ID) AS month05
              ),
              0
           )        AS hoursActual5,
       IFNULL((
                  SELECT count(1) AS month06
