       IFNULL((
                  SELECT count(1) AS month12
                  FROM (SELECT count(*)
                        FROM attendance
                        WHERE years = #{year2}
                            AND MONTHS = '12' AND CENTER_ID = #{deptname}
                           OR GROUP_ID = #{deptname}
                        GROUP BY USER_ID) AS month12
