       IFNULL((
                  SELECT count(1) AS month01
                  FROM (SELECT count(*)
                        FROM attendance
                        WHERE years = #{year1}
                            AND MONTHS = '01' AND CENTER_ID = #{deptname}
                           OR GROUP_ID = #{deptname}
                        GROUP BY USER_ID) AS month01
              ), 0) AS hoursActual1,
       IFNULL((
                  SELECT count(1) AS month02
                  FROM (SELECT count(*)
                        FROM attendance
                        WHERE years = #{year1}
                            AND MONTHS = '02' AND CENTER_ID = #{deptname}
                           OR GROUP_ID = #{deptname}
                        GROUP BY USER_ID) AS month02
