mapper.select:
select count(*) from loanapplication
where DATE_FORMAT(CREATEON,'%Y-%m-%d') = #{createon}
group by DATE_FORMAT(CREATEON,'%Y-%m-%d')

SELECT
*
FROM
loanapplication


        and processingstatus = #{processingstatus}


        and loanapno like CONCAT('%',#{loanapno},'%')


        and user_id = #{user_id}


        and center_id = #{center_id}


        and group_id = #{group_id}


        and
        owner in

            #{item}
