mapper.select:
SELECT *
FROM (
         SELECT *
         FROM logmanagement
     ) a
ORDER BY a.log_date,a.TIME_START;

select * from logmanagement where 1=1
and DATE_FORMAT(LOG_DATE, '%Y-%m-%d') >= DATE_FORMAT(#{startDate}, '%Y-%m-%d')
and DATE_FORMAT(LOG_DATE, '%Y-%m-%d') <= DATE_FORMAT(#{endDate}, '%Y-%m-%d')
and (owner in
#{item}
)

select companyprojects_id as projectid,numbers,project_name as projectname,logm.confirm,logm.unconfirm from companyprojects left join ( select project_id,sum(confirm) as confirm,sum(unconfirm) as unconfirm from (select project_id, case when confirmstatus = '1' then SUM(cast(TIME_START as decimal(12,2))) end as confirm, case when (confirmstatus = '0' or confirmstatus = '2') then SUM(cast(TIME_START as decimal(12,2))) end as unconfirm from logmanagement where status = '0'
and (owner in
#{item}
)
group by project_id,confirmstatus ) log group by project_id ) logm on logm.project_id = companyprojects.companyprojects_id where status = '4'
and (owner in
#{item}
)
union select companyprojects.companyprojects_id as projectid,companyprojects.numbers,companyprojects.project_name as projectname,logm.confirm,logm.unconfirm from companyprojects left join ( select project_id,sum(confirm) as confirm,sum(unconfirm) as unconfirm from (select project_id, case when confirmstatus = '1' then SUM(cast(TIME_START as decimal(12,2))) end as confirm, case when (confirmstatus = '0' or confirmstatus = '2') then SUM(cast(TIME_START as decimal(12,2))) end as unconfirm from logmanagement where status = '0'
and (owner in
#{item}
)
group by project_id,confirmstatus ) log group by project_id ) logm on logm.project_id = companyprojects.companyprojects_id inner JOIN (select * from Projectsystem where Projectsystem.name = #{userid}) Projectsystem on Projectsystem.companyprojects_id = companyprojects.companyprojects_id where companyprojects.status = '4' union select COMPROJECT_ID as projectid,NUMBERS,PROJECT_NAME as projectname,logm.confirm,logm.unconfirm from comproject left join ( select project_id,sum(confirm) as confirm,sum(unconfirm) as unconfirm from (select project_id, case when confirmstatus = '1' then SUM(cast(TIME_START as decimal(12,2))) end as confirm, case when (confirmstatus = '0' or confirmstatus = '2') then SUM(cast(TIME_START as decimal(12,2))) end as unconfirm from logmanagement where status = '0'
and (owner in
#{item}
)
group by project_id,confirmstatus ) log group by project_id ) logm on logm.project_id = comproject.COMPROJECT_ID where status = '4'
and (owner in
#{item}
)
union select comproject.COMPROJECT_ID as projectid,comproject.numbers,comproject.project_name as projectname,logm.confirm,logm.unconfirm from comproject left join ( select project_id,sum(confirm) as confirm,sum(unconfirm) as unconfirm from (select project_id, case when confirmstatus = '1' then SUM(cast(TIME_START as decimal(12,2))) end as confirm, case when (confirmstatus = '0' or confirmstatus = '2') then SUM(cast(TIME_START as decimal(12,2))) end as unconfirm from logmanagement where status = '0'
and (owner in
#{item}
)
group by project_id,confirmstatus ) log group by project_id ) logm on logm.project_id = comproject.COMPROJECT_ID inner JOIN (select * from prosystem where prosystem.name = #{userid}) prosystem on prosystem.COMPROJECT_ID = comproject.COMPROJECT_ID where comproject.status = '4'

select createby as projectid,sum(confirm) as confirm,sum(unconfirm) as unconfirm from ( select createby, case when confirmstatus = '1' then SUM(cast(TIME_START as decimal(12,2))) end as confirm, case when (confirmstatus = '0' or confirmstatus = '2') then SUM(cast(TIME_START as decimal(12,2))) end as unconfirm from logmanagement where status = '0' and (project_id = '' or project_id = 'PP024001') and date_format(LOG_DATE, '%Y-%m') = #{StrDate}
and (owner in
#{item}
)
group by createby,confirmstatus ) logm group by createby

select info.createby,group_concat(info.logdate) as logdate ,group_concat(info.timestart) as timestart from ( select createby,date_format(log_date,'%Y-%m-%d') as logdate,sum(time_start) as timestart from logmanagement where confirmstatus = '0' and (project_id is null or project_id = '' or project_id = 'PP024001' )
and (createby in
#{item}
)
and date_format(LOG_DATE, '%Y-%m-%d') between str_to_date(#{starttime}, '%Y-%m-%d %H') and str_to_date(#{endtime}, '%Y-%m-%d %H') group by createby,log_date ) info group by info.createby
