mapper.select:
SELECT * FROM (
	SELECT * FROM  logmanagement
	)a
ORDER BY a.log_date,a.TIME_START;

select info.createby,group_concat(info.logdate) as logdate ,group_concat(info.timestart) as timestart from ( select createby,date_format(log_date,'%Y-%m-%d') as logdate,sum(time_start) as timestart from logmanagement where confirmstatus = '0' and (project_id is null or project_id = '')
and (createby in
#{item}
)
and date_format(LOG_DATE, '%Y-%m-%d') between str_to_date(#{starttime}, '%Y-%m-%d %H') and str_to_date(#{endtime}, '%Y-%m-%d %H') group by createby,log_date ) info group by info.createby

 	update logmanagement set confirmstatus = #{confirmstatus}
	where
	confirmstatus = '0'
	and createby = #{createby}
    and date_format(LOG_DATE, '%Y-%m-%d')
    between str_to_date(#{starttime}, '%Y-%m-%d %H') and str_to_date(#{endtime}, '%Y-%m-%d %H')
