mapper.select:
select * from personalcost
where groupid = #{groupid}
and yearsantid = #{yearsantid}

SELECT * from personalcost where (USERID) in
(#{item.userid})
and yearsantid = #{yearsantid}

update.foreach:
update personalcost
CHANGERANK = #{item.changerank}, LTRANK = #{item.ltrank}, MODIFYBY = #{tokenModel.userId}, MODIFYON = #{tokenModel.expireDate}, TENANTID = #{tokenModel.tenantId},
WHERE personalcostid = #{item.personalcostid}
