mapper.select:
select count(*) from publicexpense
where REIMBURSEMENTDATE = #{reimbursementDate}
group by REIMBURSEMENTDATE

SELECT supplierinfor.SUPPLIERINFOR_ID PAYEENAME, publicexpense.JZMONTH, SUM(ot.RMB) TORMB FROM publicexpense LEFT JOIN supplierinfor ON publicexpense.payeename = supplierinfor.SUPCHINESE OR publicexpense.payeename = supplierinfor.SUPJAPANESE OR publicexpense.payeename = supplierinfor.SUPENGLISH LEFT JOIN ( SELECT otherdetails.PUBLICEXPENSE_ID, CONVERT ( SUM( RMB ), DECIMAL ( 10, 2 )) AS RMB FROM otherdetails
WHERE DEPARTMENTNAME = #{groupid}
GROUP BY otherdetails.PUBLICEXPENSE_ID ) ot ON publicexpense.PUBLICEXPENSE_ID = ot.PUBLICEXPENSE_ID WHERE 1 = 1 AND checkedWZFY = 0 #外注费用 AND publicexpense.status = '4' AND supplierinfor.SUPPLIERINFOR_ID IS NOT NULL
AND DATE_FORMAT( publicexpense.jzmonth, '%Y' ) = #{years}
GROUP BY supplierinfor.SUPPLIERINFOR_ID, publicexpense.jzmonth

SELECT
*
FROM
publicexpense
where status != '1'

    and invoiceno like CONCAT('%',#{invoiceno},'%')


    and user_id = #{user_id}


    and center_id = #{centerid}


    and group_id = #{groupid}


    and processingstatus = #{processingstatus}


    and
    owner in

        #{item}
