mapper.select:
select expatriatesinfor_id,group_id,suppliername,account from expatriatesinfor
where 1=1

    and (account not in

        #{item}

    )

select jobnumber,punchcardrecord_date,eventno from punchcardrecorddetailbp
where jobnumber = #{jobnumber}
and user_id like CONCAT('%',#{user_id},'%')
and  date_format(punchcardrecord_date, '%Y-%m-%d') = #{punchcardrecord_date}
order by punchcardrecord_date

mapper.delete:
DELETE FROM punchcardrecordbp
WHERE date_format(punchcardrecord_date, '%Y-%m-%d') BETWEEN #{startDate} and #{endDate}

    AND jobnumber = #{staffNo,jdbcType=VARCHAR}

DELETE FROM punchcardrecorddetailbp
WHERE date_format(punchcardrecord_date, '%Y-%m-%d') BETWEEN #{startDate} and #{endDate}

    AND jobnumber = #{staffNo,jdbcType=VARCHAR}
