mapper.select:
SELECT theme.THEMEINFOR_ID themeinforId, theme.THEMENAME themeName, theme.TOOLSORGS customerName, SUM(detail.AMOUNT4) aprilPlan, SUM(detail.AMOUNT5) mayPlan, SUM(detail.AMOUNT6) junePlan, SUM(detail.AMOUNT7) julyPlan, SUM(detail.AMOUNT8) augustPlan, SUM(detail.AMOUNT9) septemberPlan, SUM(detail.AMOUNT10) octoberPlan, SUM(detail.AMOUNT11) novemberPlan, SUM(detail.AMOUNT12) decemberPlan, SUM(detail.AMOUNT1) januaryPlan, SUM(detail.AMOUNT2) februaryPlan, SUM(detail.AMOUNT3) marchPlan FROM themeinfor theme LEFT JOIN themeplandetail detail ON theme.THEMEINFOR_ID = detail.THEMEINFOR_ID
AND detail.CENTER_ID = #{deptId}
AND detail.YEAR = #{year}
GROUP BY theme.THEMEINFOR_ID

mapper.sql:
SELECT SUM(count.CLAIMAMOUNT) amount, DATE_FORMAT(count.DELIVERYDATE, '%Y') year, DATE_FORMAT(count.DELIVERYDATE, '%c') month FROM contractapplication conapp LEFT JOIN award award ON conapp.CONTRACTNUMBER = award.CONTRACTNUMBER LEFT JOIN contractnumbercount count ON conapp.CONTRACTNUMBER = count.CONTRACTNUMBER WHERE award.STATUS = '4'
AND conapp.GROUP_ID = #{deptId}
AND conapp.CONTRACTTYPE not in ('HT008002', 'HT008004', 'HT008006', 'HT008008') GROUP BY DATE_FORMAT(count.DELIVERYDATE, '%Y'),DATE_FORMAT(count.DELIVERYDATE, '%c')

SELECT SUM(reunite.DISTRIAMOUNT) amount, DATE_FORMAT(reunite.DELIVERYDATE, '%Y') year, DATE_FORMAT(reunite.DELIVERYDATE, '%c') month FROM contractapplication conapp LEFT JOIN award award ON conapp.CONTRACTNUMBER = award.CONTRACTNUMBER LEFT JOIN awardreunite reunite ON conapp.CONTRACTNUMBER = reunite.CONTRACTNUMBER WHERE award.STATUS = '4'
AND reunite.REALDEPARTMENT = #{deptId}
AND conapp.CONTRACTTYPE in ('HT008002', 'HT008004', 'HT008006', 'HT008008') GROUP BY DATE_FORMAT(reunite.DELIVERYDATE, '%Y'),DATE_FORMAT(reunite.DELIVERYDATE, '%c')

mapper.select:
SELECT reve.id id, reve.themeinfor_id, theme.THEMENAME themeName, theme.TOOLSORGS customerName, reve.save_date, reve.dept_id, reve.april_plan, reve.april_forecast, (SELECT temp.amount FROM () as temp where temp.year = #{year} AND temp.month = 4) + (SELECT unit.amount FROM () as unit where unit.year = #{year} AND unit.month = 4) aprilActual, reve.may_plan, reve.may_forecast, (SELECT temp.amount FROM () as temp where temp.year = #{year} AND temp.month = 5) + (SELECT unit.amount FROM () as unit where unit.year = #{year} AND unit.month = 5) mayActual, reve.june_plan, reve.june_forecast, (SELECT temp.amount FROM () as temp where temp.year = #{year} AND temp.month = 6) + (SELECT unit.amount FROM () as unit where unit.year = #{year} AND unit.month = 6) juneActual, reve.july_plan, reve.july_forecast, (SELECT temp.amount FROM () as temp where temp.year = #{year} AND temp.month = 7) + (SELECT unit.amount FROM () as unit where unit.year = #{year} AND unit.month = 7) julyActual, reve.august_plan, reve.august_forecast, (SELECT temp.amount FROM () as temp where temp.year = #{year} AND temp.month = 8) + (SELECT unit.amount FROM () as unit where unit.year = #{year} AND unit.month = 8) augustActual, reve.september_plan, reve.september_forecast, (SELECT temp.amount FROM () as temp where temp.year = #{year} AND temp.month = 9) + (SELECT unit.amount FROM () as unit where unit.year = #{year} AND unit.month = 9) septemberActual, reve.october_plan, reve.october_forecast, (SELECT temp.amount FROM () as temp where temp.year = #{year} AND temp.month = 10) + (SELECT unit.amount FROM () as unit where unit.year = #{year} AND unit.month = 10) octoberActual, reve.november_plan, reve.november_forecast, (SELECT temp.amount FROM () as temp where temp.year = #{year} AND temp.month = 11) + (SELECT unit.amount FROM () as unit where unit.year = #{year} AND unit.month = 11) novemberActual, reve.december_plan, reve.december_forecast, (SELECT temp.amount FROM () as temp where temp.year = #{year} AND temp.month = 12) + (SELECT unit.amount FROM () as unit where unit.year = #{year} AND unit.month = 12) decemberActual, reve.january_plan, reve.january_forecast, (SELECT temp.amount FROM () as temp where temp.year = #{year} + 1 AND temp.month = 1) + (SELECT unit.amount FROM () as unit where unit.year = #{year} + 1 AND unit.month = 1) januaryActual, reve.february_plan, reve.february_forecast, (SELECT temp.amount FROM () as temp where temp.year = #{year} + 1 AND temp.month = 2) + (SELECT unit.amount FROM () as unit where unit.year = #{year} + 1 AND unit.month = 2) februaryActual, reve.march_plan, reve.march_forecast, (SELECT temp.amount FROM () as temp where temp.year = #{year} + 1 AND temp.month = 3) + (SELECT unit.amount FROM () as unit where unit.year = #{year} + 1 AND unit.month = 3) marchActual, reve.CREATEBY createby, reve.CREATEON createon, reve.MODIFYBY modifyby, reve.MODIFYON modifyon, reve.OWNER owner, reve.STATUS status, reve.TENANTID tenantid FROM revenue_forecast reve LEFT JOIN themeinfor theme ON reve.themeinfor_id = theme.THEMEINFOR_ID
DATE_FORMAT(reve.save_date, '%Y%M') = ()
AND reve.dept_id = #{deptId}
