mapper.update:
update RULING
set APPLIOCCUTION = APPLIOCCUTION + #{useMoney,jdbcType=DECIMAL}
  ,VERSION = VERSION + 1
WHERE RULING_ID = #{ruling_id}
  AND VERSION = #{oldVersion};

update RULING
set APPLIOCCUTION = APPLIOCCUTION - #{renMoney,jdbcType=DECIMAL}
  ,VERSION = VERSION + 1
WHERE RULING_ID = #{ruling_id}
  AND VERSION = #{oldVersion};

update RULING
set ACTUALRESIDUAL = ACTUALRESIDUAL - #{offMoney,jdbcType=DECIMAL}
  ,ACTUALCONSUMPTION = ACTUALCONSUMPTION + #{offMoney,jdbcType=DECIMAL}
  ,VERSION = VERSION + 1
WHERE RULING_ID = #{ruling_id}
  AND VERSION = #{oldVersion};

update RULING
set APPLIOCCUTION = APPLIOCCUTION + #{useMoney,jdbcType=DECIMAL}
  ,VERSION = VERSION + 1
WHERE CODE = #{code}
  AND YEARS = #{years}
  AND DEPART = #{depart}
  AND VERSION = #{oldVersion};

update RULING
set APPLIOCCUTION = APPLIOCCUTION - #{renMoney,jdbcType=DECIMAL}
  ,VERSION = VERSION + 1
WHERE CODE = #{code}
  AND YEARS = #{years}
  AND DEPART = #{depart}
  AND VERSION = #{oldVersion};
