mapper.select:
select USER_ID from wages
where wages.CREATEONYM = #{yearmonth}
and wages.actual = '0'

    and USER_ID = #{userid}

select * from wages
 where wages.CREATEONYM = #{yearmonth}
AND wages.actual = '0'

    and USER_ID = #{userid}

update wages
set grantstatus = '1'
,MODIFYBY = #{user_id}
,MODIFYON = NOW()
where giving_id = #{givingId}

select COMTOTALWAGES from wages where CREATEONYM like CONCAT('%',#{createonym},'%')
and USER_ID = #{userid}
and COMTOTALWAGES is not null

select TOTAL3 from wages where CREATEONYM like CONCAT('%',#{createonym},'%')
and USER_ID = #{userid}
and TOTAL3 is not null

select ACTUAL
from wages
where  CREATEONYM = #{createonym}

    and  USER_ID =  #{userid}
